const express=require("express"),{graphqlHTTP:graphqlHTTP}=require("express-graphql"),{buildSchema:buildSchema}=require("graphql"),schema=buildSchema("\n  type Query {\n    hello: String\n    user(id: Int!): User\n  }\n\n  type User {\n    id: Int\n    name: String\n    age: Int\n  }\n"),users=[{id:1,name:"Alice",age:28},{id:2,name:"Bob",age:34}],root={hello:()=>"Hello, world!",user:({id:e})=>users.find((r=>r.id===e))},app=express();app.use("/graphql",graphqlHTTP({schema:schema,rootValue:root,graphiql:!0})),app.listen(4e3,(()=>{console.log("Server is running on http://localhost:4000/graphql")}));